import React, { useState, useEffect } from 'react';
import { Container, Jumbotron, Button, Form, FormControl, Row, Col, Modal } from 'react-bootstrap';
import styled from 'styled-components';
import Team_schedule from './Team_schedule';
import SpanTagStyle from '../constant/SpanTagStyle';
import { Link } from 'react-router-dom';

const MainCardStyle = styled.div`
  width: 100%;
  margin: auto;
`;

const SlideStyle = styled.div`
	margin-top:15%;
	margin-bottom:4%;
`;

const MyTeam = () => {

	const id = 1;
	const url2 = "/Team_schedule/" + id;

	const inputHandle = (e) => {
		setSearchUser({
			...searchUser,
			[e.target.name]: e.target.value,
		});
	};

	// Ïó¨Í∏∞Î∂ÄÌÑ∞ state ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- 

	// ÏïÑÎûò 3Í∞úÏùò state - team datail info fetchÏóêÏÑú Î∞õÏïÑÏò¥
	const [team, setTeam] = useState([]);
	const [owner, setOwner] = useState([]);
	const [users, setUsers] = useState([]);
	const { explaintation, name } = team;

	const [partys, setPartys] = useState([]);
	const [battles, setBattles] = useState([]);
	const [schedule, setSchedule] = useState([]);
	// Ïù¥Î†áÍ≤å ÏúÑÎ°ú 6Í∞ú state useEffect fetchÏóêÏÑú Î∞õÏïÑÏò¥

	// modal state and function. show : ÌåÄÏõê Ï¥àÎåÄ / show2 : Í≤ΩÍ∏∞ ÏÉÅÏÑ∏Ï†ïÎ≥¥ page
	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);


	const [battleIdInModal, setBattleIdInModal] = useState(0);
	const [battleIdInModalData, setBattleIdInModalData] = useState({});

	const [show3, setShow3] = useState(false);
	const handleClose3 = () => setShow3(false);
	const handleShow3 = (id) => {
		battles.map((res) => (
			res.id === id
				?
				setBattleIdInModalData(res)
				: null
		))
		setBattleIdInModal(id);
		setShow3(true);
	};

	const [show2, setShow2] = useState(false);
	const handleClose2 = () => setShow2(false);
	const handleShow2 = (id, myTeam, jteam, location, matchDate) => {
		setShow2(true);
		setDeId({
			id: id,
			myTeam: myTeam,
			jteam: jteam,
			location: location,
			matchDate: matchDate
		});
	};

	// ÌåÄÏõê Ï¥àÎåÄÌï† Îïå ÏÇ¨Ïö©ÎêòÎäî state. ÏïÑÎûò statusÎäî Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä Ï†ÅÌï©Ìïú ÏßÄ checkÌïòÎäî Ïö©ÎèÑ
	const [searchUser, setSearchUser] = useState({
		nickname: "",
		location: "",
		position: "",
		id: null,
	});

	const [searchUserStatue, setSearchUserStatus] = useState("enter nickname and search!");

	const [isSearch, setIsSearch] = useState(false);

	const [deId, setDeId] = useState({
		id: null,
		myTeam: null,
		jteam: null,
		location: null,
		matchDate: null
	}); // ÏßÑÌñâÌïòÍ∏∞Î°ú Í≤∞Ï†ï Îêú Í≤ΩÍ∏∞ ÏûêÏÑ∏Ìûà Î≥¥Í∏∞ Î™®Îã¨Ïóê Í∞ÄÏ†∏Í∞à data

	// ÌåÄÏõê ÏÑ†ÌÉù state
	const [rteam, setRteam] = useState(["ÌåÄÏû•"]);

	const rteamplus = (id) => {
		alert("Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§");
		setRteam([
			...rteam,
			id
		]);
		console.log(rteam);
	}

	const memberCheck = () => {
		alert(rteam);
	}
	// Ïó¨Í∏∞Í∞ÄÏßÄ state ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- 

	const inviteMember = (userid) => {	// ÌåÄÏõê Ï¥àÎåÄ Ìï®Ïàò (Í≤ÄÏÉâ Îã§Ïùå Ï¥àÎåÄ)
		fetch(`http://localhost:8000/user/apply2/${userid}`, {
			method: "post",
			headers: {
				'Content-Type': "application/json; charset=utf-8",
				'Authorization': localStorage.getItem("Authorization")
			}
		}).then((res) => res.text())
			.then(res => {
				if (res === "ok") {
					handleClose();
					alert("ÌåÄÍ∞ÄÏûÖ ÏöîÏ≤≠ ÏôÑÎ£å");
				}
				else alert("ÌåÄÍ∞ÄÏûÖ ÏöîÏ≤≠ Ïã§Ìå®");
			});
	};

	const searchUserfunction = (nick) => {	// ÌåÄÏõê Í≤ÄÏÉâ Ìï®Ïàò (Í≤ÄÏÉâ Îã§Ïùå Ï¥àÎåÄ)
		alert(nick + " Í≤ÄÏÉâÎêòÏóàÏäµÎãàÎã§");
		// fetchÎ°ú Í≤ÄÏÉâÌï¥ÏÑú searchUserÏóê ÎÑ£Í∏∞
		fetch(`http://localhost:8000/nicknameDetail/${nick}`, {
			method: "get",
		}).then((res) => {
			if (res.status === 200) {
				setSearchUserStatus("search complete");
				return res.json()
